class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        int n = a.size(), i, j, st = 0, zd = 0, can[50005], res = 0;
        sort(a.begin(), a.end());
        a.resize(distance(a.begin(), unique(a.begin(), a.end())));
        zd = a.back();
        for (j = 0; j <= zd; ++j) can[j] = false;
        can[0] = true;
        for (i = 0; i <= zd; ++i)
            if (can[i]){
                if (i == zd) res = max(res, i);
                else res = max(res, i + zd);
                while (st < n && a[st] <= i) ++st;
                for (j = st; j < n && i + a[j] <= zd; ++j)
                    can[i + a[j]] = true;
            }

        return res;
    }
};
