class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        using bset = bitset<131090>;
        sort(a.begin(), a.end());
        a.erase(unique(a.begin(), a.end()), a.end());
        int res = 0;
        bset f;
        f.set(0);
        int n = a.size();
        for(int i = 0; i < n; i++){
            f = f | ((f ^ ((f>>(a[i]))<<(a[i])))<<a[i]);
        }
        for(int i = 0; i < 131090; i++) if(f[i]) res = i;
        return res;
    }
};
