class Solution {
public:
    int n;
    std::bitset <100005> f, cur; 
    int maxTotalReward(vector<int>& a) {
        n = a.size();
        f[0] = 1;
        cur[0] = 1; 
        int now = 0;
        std::sort(a.begin(), a.end());
        for(int i = 0; i < n; ++i){
            int v = a[i];
            while(now < v - 1) cur[++now] = 1;
            f = f | ((f & cur) << v);
        }
        int ans = 0;
        for(int i = 0; i <= 100000; ++i) if(f[i] == 1) ans = i;
        return ans;
    }
};
