const int N = 1e5 + 7;

class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        int n = a.size();
        sort(a.begin(), a.end());
        bitset<N> dp;
        dp.set(0);
        for(int i = 0; i < n; i++) {
            int x = a[i];
            dp |= ((dp << (N - x)) >> (N - x)) << x;
        }
        for(int i = N - 1; i >= 0; i--) {
            if(dp.test(i)) return i;
        }
        return 0;
    }
};
