class Solution:
    def maxTotalReward(self, rewardValues: List[int]) -> int:
        def smmax(x):
            if mem[x] is not None:
                return mem[x]
            p = bisect_left(val, x) - 1
            if p < 0:
                mem[x] = 0
                return 0
            ans = -1
            while p >= 0 and (vp:=val[p]) > x // 2:
                ans = max(ans, vp + smmax(x - vp))
                p -= 1
                if ans == x - 1:
                    break
            # print(x, ans)
            if p >= 0 and (vp:=val[p]) <= x // 2:
                ans = max(ans, vp + smmax(vp))
            mem[x] = ans
            return ans
        tg = max(rewardValues)
        # tg = 50_000
        val = []
        pos = [0] * (tg + 1)
        mem = [None] * (tg + 1)
        for i in rewardValues:
            pos[i] = 1
        for i in range(tg + 1):
            if pos[i]:
                val.append(i)
        # print(val, pos)
        # smmax(tg)
        # print(mem)
        return tg + smmax(tg)
