class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        int n = rewardValues.size();
        sort(rewardValues.begin(), rewardValues.end());
        bitset<50010> bs;
        bitset<50010> msk;
        bs[0] = 1;
        int now = 0;
        int ans = -1;
        for(int i = 0; i < n - 1; i++){
            int x = rewardValues[i];
            while(now < x){
                msk[now] = 1;
                now++;
            }
            bitset<50010> bs2 = bs & msk;
            bs = bs | (bs2 << x);
        }
        // cout << "bs is " << bs.to_string() << " " << rewardValues[n - 1] << endl;
        for(int i = 0; i < rewardValues[n - 1]; i++){
            if(bs[i]){
                ans = max(ans, i + rewardValues[n - 1]);
            }
        }
        return ans;
    }
};
