class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {

       //ewardValues.clear();
       //for (int i = 1; i <= 50000; ++i)
        //  rewardValues.push_back(i);

        sort(rewardValues.begin(), rewardValues.end());
       // cout << rewardValues.size() << endl;

        int res = 0;
        bitset<LEN> dp;
        bitset<LEN> mask(0);
        bitset<LEN> tmp;
        dp[0] = 1;
        int i = 0;
       // int prev = -1;
        for (auto& v : rewardValues)
        { 
            //if (prev == v) continue;
            for (; i < v; ++i) mask[i] = 1;            
            tmp = dp & mask;
            dp = dp | (tmp << v);   
           // prev = v;
        }

        for (int i = 0; i < dp.size(); ++i)
            if (dp[i] == 1) res = max(res, i);        
        return res;
    }
};
