#include <bits/stdc++.h>
#define ll long long
using namespace std;


class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        bitset<100005> ans;
        bitset<100005> temp;
        temp = 1;
        ans = 1;
        sort(rewardValues.begin(), rewardValues.end());

        for (auto&i : rewardValues){
            temp = ans;
            temp = (temp << (100005-i));
            temp = temp >> (100005-i);
            temp = temp | (temp << i);
            ans |= temp;
        }
        ll maxi = rewardValues[rewardValues.size()-1];
        ll ansi = 0;
        for (ll i=0; i<(maxi*2+5); i++){
            if (ans[i]){
                ansi = max(ansi, i);
            }
        }
        return ansi;
    }
};
