class Solution {
public:

    bool used[100000] = {false};

    int maxTotalReward(vector<int>& b) {
        sort(b.begin(),b.end());
        vector<int> a;
        a.push_back(b[0]);
        for(int i = 1; i < b.size(); ++i){
            if(b[i] != a[(int)a.size() - 1]){
                a.push_back(b[i]);
            }
        }
        used[0] = true;
        int mx = 0;
        int ans = 0;
        int mx2 = 0;
        for(int i = 0; i < a.size(); ++i){
            for(int j = a[i] - 1; j >= 0; --j){
                if(used[j]){
                    ans = max(ans, j + a[i]);
                    break;
                }
            }
            mx2 = mx;
            for(int j = min(50000 - a[i],a[i] - 1); j >= 0; --j){
                if(used[j] && j + a[i] <= 50000){
                    mx2 = max(mx2, j + a[i]);
                    used[j + a[i]] = 1;
                }
            }
            mx = mx2;
        }

        return ans;

    }
};
