class Solution:
    def maxTotalReward(self, a: List[int]) -> int:
        a = list(set(a))
        n = len(a)
        a.sort()

        @cache
        def d(i, mm):
            if i == -1:
                return 0
            if mm == 0:
                return 0
            if a[i] == mm:
                return mm

            ans = 0

            lim = min(a[i] - 1, mm - a[i])
            j = bisect_right(a, lim) - 1
            ans = max(ans, d(j,  lim) + a[i] )

            if ans == mm:
                return ans

            if ans == 2 * a[i] - 1:
                return ans


            ans = max(ans, d(i - 1, mm))

            return ans

        ans = d(n - 1, inf)
        #print(d.cache_info())
        d.cache_clear()

        return ans
