class Solution:
    def maxTotalReward(self, nums: List[int]) -> int:
        nums = list(set(nums))
        n = len(nums)
        nums.sort()
        se = set(nums)

        @cache
        def closestLE(num):
            if num in se or num == 0:
                return num
            ind = bisect_left(nums, num)
            ans = 0
            for i in range(ind):
                ans = max(ans, nums[i] + closestLE(min(nums[i] - 1, num - nums[i])))
            return ans
        return nums[-1] + closestLE(nums[-1] - 1)
