class Solution {
public:
  int maxTotalReward(vector<int>& a) {
    sort(begin(a), end(a));
    bitset<100010> f, suffix;
    int cur = 0;
    f[0] = 1;
    for (int x : a)
    {
      while (cur < x)
        suffix[cur++] = 1;
      f |= (f & suffix) << x;
    }
    for (int i = 1e5; i; i--)
      if (f[i])
        return i;
    return 0;
  }
};
