class Solution {
public:
    int maxTotalReward(vector<int>& rin) {
        r = std::move(rin);
        int n = r.size();
        sort(r.begin(), r.end());
        int res = r.back();
        s.clear();
        s.resize(res+1, 0);
        for (int i = 0; i < n-1; ++i)
            s[r[i]] = 1;
        p.resize(res+1, 0);
        for (int i = 1; i < res; ++i)
        {
            p[i] = p[i-1];
            while (r[p[i]] <= i)
                ++p[i];
        }
        for (int i = res-1; i > 0; --i)
            if (calc(i))
                return res + i;
        return res;
    }

    bool calc(int v)
    {
        if (s[v] != 0)
            return s[v] == 1;
        int idx = p[v]-1;
        while (idx > 0 && r[idx] * 2 > v)
            if (calc(v - r[idx]))
            {
                s[v] = 1;
                return true;
            }
            else
            {
                --idx;
            }
        s[v] = 2;
        return false;
    }

    vector<int> s;
    vector<int> r;
    vector<int> p;
};
