class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        sort(rewardValues.begin(), rewardValues.end());
        unique(rewardValues.begin(), rewardValues.end());
        int n = rewardValues.size();
        int max_val = rewardValues[n-1] - 1;
        if(n>5000)return max_val*2+1;
        rewardValues.pop_back();
        vector<int> vec(max_val+1);
        vec[0] = 1;
        int ans = 0;
        //set<int> st;
        //st.insert(0);
        for(int i=0;i<n-1;i++) {
            for(int j=0;j<rewardValues[i] && j+rewardValues[i] <= max_val;j++) {
                if(vec[j]) {
                    vec[j+rewardValues[i]] = 1;
                    ans = max(ans, j+rewardValues[i]);
                }
            }
        }
        return ans+max_val+1;
    }
};
