class Solution {
public:
    int maxTotalReward(vector<int>& r) {
        int n=r.size();
        sort(r.begin(),r.end());
        int mx=r[n-1];
        bitset<100005> bs,tmp,suf;
        bs[0]=1;
        tmp[0]=1;
        suf[0]=1;
        int prev=1;
        int ans=0;
        for(int i=0;i<n;i++){
            for(int j=prev;j<r[i];j++){
                suf[j]=1;
            }
            tmp=tmp&suf;
            // cout<<tmp<<" ";
            tmp|=(tmp<<r[i]);
            bs|=tmp;
            tmp|=bs;
            // cout<<bs<<"\n";
            prev=r[i];


        }
        for(int i=2*mx;i>=0;i--){
            if(bs[i]){
                return i;
            }
        }
        return ans;
    }
};
