class Solution:
    def maxTotalReward(self, a: List[int]) -> int:
        a = sorted(set(a))
        n = len(a)
        t = 0
        @cache
        def f(x):
            if not x:
                return True
            j = bisect_right(a,x)-1
            while j >= 0 and x < 2*a[j]:
                if f(x-a[j]):
                    return True
                j -= 1
            return False
        for x in reversed(range(2*a[-1]+1)):
            if f(x):
                f.cache_clear()
                return x
