class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        sort(rewardValues.begin(), rewardValues.end());
        int n = rewardValues.size();
        const int N = 1E5;
        bitset<N + 1> dp, all;
        for (int i = 0; i <= N; i++) {
            all[i] = 1;
        }
        dp[0] = 1;
        for (int i = 0; i < n; i++) {
            dp |= (dp << rewardValues[i]) & (all >> (N - 2 * rewardValues[i] + 1));
        }
        int ans = 0;
        for (int i = 0; i <= N; i++) {
            if (dp[i]) ans = i;
        }
        return ans;
    }
};
