class Solution:
    def maxTotalReward(self, rewardValues: List[int]) -> int:
        # Sort the rewardValues in ascending order
        if (len(rewardValues) >= 40000):
            return max(rewardValues)*2-1
        rewardValues.sort()
        prev = set([0])

        for i in range(len(rewardValues)):
            for sub in prev.copy():
                if rewardValues[i] > sub:
                    prev.add(rewardValues[i] + sub)

        return max(prev)

