class Solution {
public:
    int n;
    vector<int> z;
    vector<int> dp;
    int possible(int x){
        if(dp[x]!=-1)return dp[x];
        int ind=upper_bound(z.begin(),z.end(),x)-z.begin();
        if(ind==0)return dp[x]=0;
        ind--;
        dp[x]=0;
        for(int j=ind;j>=0;j--){
            int ans=z[j];
            int rem=x-z[j];
            ans+=possible(min(rem,z[j]-1));
            dp[x]=max(dp[x],ans);
            if(dp[x]==x)break;
        }
        return dp[x];
    }
    int maxTotalReward(vector<int>& rewardValues) {

        set<int> y;
        for(int x:rewardValues)y.insert(x);

        for(int x:y)z.push_back(x);
        n=z.size();
        dp=vector<int>(z[n-1],-1);
        int ans=z[n-1];
        ans+=possible(z[n-1]-1);


        return ans;
    }
};
