type cache struct {
	maxSeen int
}

func (c *cache) maxTotalReward(rewardValues []int, reward, idx int, budgetLeft int) int {
	out := reward
	if budgetLeft == 0 || reward+budgetLeft <= c.maxSeen {
		return out
	}
	for i, r := range rewardValues[idx:] {
		if budgetLeft >= r {
			result := c.maxTotalReward(rewardValues, reward+r, idx+i+1, min(budgetLeft-r, r-1))
			c.maxSeen = max(c.maxSeen, result)
			out = max(out, result)
		}
	}
	return out
}

func maxTotalReward(rewardValues []int) int {
	sort.Slice(rewardValues, func(i, j int) bool {
		return rewardValues[i] > rewardValues[j]
	})
	c := new(cache)
	return c.maxTotalReward(rewardValues, rewardValues[0], 1, rewardValues[0]-1)
}
