class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        bitset<50001> bs(1);
        bitset<50001> bss(1);
        int n=rewardValues.size();
        int last=0;
        sort(rewardValues.begin(),rewardValues.end());
        for(int i=0;i<n-1;i++){
            for(int j=last+1;j<rewardValues[i];j++){
                bss[j]=1;
            }
            last=rewardValues[i]-1;
            bs|=((bs&bss)<<rewardValues[i]);
        }
        for(int i=rewardValues[n-1]-1;i>=0;i--){
            if(bs[i]){
                return rewardValues[n-1]+i;
            }
        }
        return 5;
    }
};
