class Solution:
    def maxTotalReward(self, a: List[int]) -> int:
        bit = 1
        mx = max(a)
        present = set(a)
        till_now = 1
        ans = 0
        for i in range(1, 2 * mx + 1):
            if(i in present):
                nbit = (till_now << i)
                bit |= nbit
                ans = i
            if(bit & (1 << i)):
                till_now |= (1 << i)
                ans = i
        return ans
