class Solution {
    public int maxTotalReward(int[] rewardValues) {
        Arrays.sort(rewardValues);
        int n = rewardValues.length;
        int max = rewardValues[n - 1];
        boolean[] exist = new boolean[max * 2];
        exist[rewardValues[0]] = true;
        for (int i = 1; i < n; i++) {
        	if (rewardValues[i] == rewardValues[i - 1])
        		continue;
        	int now = rewardValues[i];
        	exist[now] = true;
        	for (int j = 1; j < now; j++) {
        		if(exist[j])
        			exist[j + now] = true;
        	}
        }
        for (int i = max * 2 - 1; i >= 0; i--) {
        	if (exist[i])
        		return i;
        }
        return -1;
    }
}
