int solve(vector<int> &a, int m) {
    bitset<50000> f;
    f[0] = 1;
    bitset<50000> mask;
    int i = 0;
    for (int x: a) {
        while (i < x && i < f.size()) mask[i] = 1, i++;
        f |= (f & mask) << x;
    }
    for (int j = m - 1; j >= 0; --j) {
        if (f[j]) {
            return j;
        }
    }
    return 0;
}

class Solution {
public:
    int maxTotalReward(vector<int> &a) {
        ranges::sort(a);
        a.erase(unique(a.begin(), a.end()), a.end());
        int m = a.back();
        a.pop_back();
        return solve(a, m) + m;
    }
};
