class Solution {
    unordered_map<int, bool> note;
public:
    int maxTotalReward(vector<int>& rewardValues) {
        UN(rewardValues);
        const int len = rewardValues.size();
        if(rewardValues.size() == 1) return rewardValues[len-1];
        int l = 0, R = rewardValues[len-1] * 2 - 1;
        vector<bool> dp(R+1, false);
        dp[0] = true;
        for(int i: rewardValues) dp[i] = true;
        for(int n = 1; n <= R; ++n) {
            if(dp[n]) continue;
            while(rewardValues[l] * 2 <= n) ++l;
            for(int i = l; i < len && n-rewardValues[i] >= 0; ++i) {
                if(dp[n-rewardValues[i]]) {
                    dp[n] = true;
                    break;
                }
            }
        }
        for(int i = R; i >= 0; --i) {
            if(dp[i]) return i;
        }
        return 0;
    }
};
