class Solution:
    def maxTotalReward(self, rewardValues: List[int]) -> int:
        vs = sorted(rewardValues)

        @cache
        def canSumV(index, s):
            while vs[index] > s:
                index -= 1
                if index == -1:
                    return False
            while index >= 0:
                x = vs[index]
                if x == s:
                    return True
                elif x > s//2 and index > 0:
                    if canSumV(index, s-x):
                        return True
                index -= 1
            return False

        y,i = vs[-1],len(vs)-1
        while vs[i] >= y:
            i -= 1
            if i == -1:
                return y
        for v in range(y-1, vs[i], -1):
            if canSumV(i, v):
                return v + y
        else:
            return vs[i] + y
