class Solution {
	set<int> Set;
	unordered_set<int> Now;
	vector<int> Base;
public:
    int maxTotalReward(vector<int>& rewardValues) {
        int i;
		int N = rewardValues.size();

		for(i = 0; i < N; i++)
		{
			Set.insert(rewardValues[i]);
		}

		for(auto it : Set)
		{
			Base.push_back(it);
		}

		int Result = Base.back();
		int Max = Base.back();

		if(Base.size() >= 10000)
		{
			return (2 * Max) - 1;
		}

		Now.insert(0);

		for(auto it : Base)
		{
			vector<int> Temp;

			for(auto it1 : Now)
			{
				if(it1 >= it)
				{
					continue;
				}

				if(Max <= (it + it1))
				{
					Result = max(Result, (it + it1));
				}
				else
				{
					Temp.push_back(it + it1);
				}
			}

			for(auto it1 : Temp)
			{
				Now.insert(it1);
			}
		}

		return Result;
    }
};
