public class Solution {
    public int MaxTotalReward(int[] rewardValues) {
        Array.Sort(rewardValues);
        int n = rewardValues.Length;
        int lastReward = rewardValues[n - 1];
        int max = 0;
        bool[] totals = new bool[lastReward];
        totals[0] = true;
        for (int i = 0; i < n - 1; i++) {
            int reward = rewardValues[i];
            for (int x = 0; x < Math.Min(reward, lastReward - reward); x++) {
                if (totals[x]) {
                    totals[x + reward] = true;
                    max = Math.Max(max, x + reward);
                }
            }
        }
        return max + lastReward;
    }
}
