class Solution {
    public int maxTotalReward(int[] rewardValues) {
        int max = 0;
        for(int i = 0; i < rewardValues.length; i++){
            max = Math.max(max, rewardValues[i]);
        }
        boolean[] arr = new boolean[max + 1];
        boolean[] org = new boolean[max + 1];
        for(int i = 0; i < rewardValues.length; i++){
            arr[rewardValues[i]] = true;
            org[rewardValues[i]] = true;
        }
        int lastMax = 0, res = 0;
        for(int i = 1; i < arr.length; i++){
            if(!org[i]){
                if(arr[i]){
                    lastMax = i;
                }
                continue;
            }
            for(int j = Math.min(i - 1, max - i - 1); j > 0; j--){
                if(!arr[j]){
                    continue;
                }
                res = Math.max(res, j + i);
                if(j + i < arr.length){
                    arr[j + i] = true;
                }
            }
            res = Math.max(res, i + lastMax);
            lastMax = i;
        }
        return Math.max(max, res);
    }
}
