public class Solution {
    public int MaxTotalReward(int[] rewardValues) {
        int n = rewardValues.Length;
        Array.Sort(rewardValues);
        List<int> uniqueRewards = new();
        uniqueRewards.Add(rewardValues[0]);
        for (int i = 1; i < n; i++)
        {
            if (rewardValues[i] != uniqueRewards.Last())
            {
                uniqueRewards.Add(rewardValues[i]);
            }
        }
        n = uniqueRewards.Count;
        bool[] visited = new bool[uniqueRewards[n-1]*2];
        visited[0] = true;

        for (int i = 0; i < n; i++)
        {
            for (int j = 0; j < uniqueRewards[i]; j++)
            {
                if (visited[j])
                {
                    visited[j + uniqueRewards[i]] = true;
                }
            }
        }
        int max = 2 * uniqueRewards[n-1] - 1;
        while (!visited[max])
        {
            max--;
        }

        return max;
    }
}
