class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        sort(rewardValues.begin(),rewardValues.end());
        rewardValues.resize(unique(rewardValues.begin(),rewardValues.end())-rewardValues.begin());
        bitset<100005> b(0), ans(0);
        ///ans[100005];
        //fill(ans,ans+100005,0);
        ans[0]=1;
        b[0]=1;
        int now=0; 
        for(auto it:rewardValues){
            while(now<it){
                if(ans[now])
                b[now]=1;
                now++;
            }
            ans|=b<<it;
        }
        for(int i = 100000;i>=0;i--){
            if(ans[i])return i;
        }
        return 0;
    }
};
