class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        const int N = 2 * (5e4 + 10);
        bitset<N> ones, can;
        can[0] = 1;
        sort(a.begin(), a.end());
        int ptr = -1;
        for (int i : a) {
            while (ptr + 1 < i) {
                ones[++ptr] = 1;
            }
            can |= (can & ones) << i;
        }

        for (int i = N - 1; i >= 0; --i) {
            if (can[i]) {
                return i;
            }
        }
        return -1;
    }
};
