class Solution {
public:
    int maxTotalReward(vector<int>& r) {
        sort(r.begin(),r.end());
        const int MX = 1e5;
        bitset<MX> dp, f;
        f.flip();
        dp[0] = 1;
        for (int x : r) {
            dp |= (dp & (f >> (MX - x))) << x;
        }
        int ans = MX - 1;
        for (;!dp[ans] && ans > 0;--ans);
        return ans;
    }
};
