class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        const int MAXS = 100001;
        bitset<100001> dp;
        dp[0] = 1;
        sort(rewardValues.begin(), rewardValues.end());
        int n = rewardValues.size();
        for (int cur : rewardValues) {
            int amt = MAXS - cur;
            dp |= ((dp << amt) >> amt) << cur;
        }
        int ans = 0;
        for (int i = 1; i <= 100000; i++) {
            if (dp[i]) {
                ans = i;
            }
        }
        return ans;
    }
};
