class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        sort(rewardValues.begin(), rewardValues.end());
        bitset<100005> dp{};
        bitset<100005> ones{};
        ones.set();
        dp[0] = true;
        for (auto rewardVal : rewardValues) {
            dp |= ((dp & (ones << rewardVal).flip()) << rewardVal);
        }

        for (int i = dp.size()-1; i >= 0; i--) {
            if (dp[i]) return i;
        }

        //never
        return -1;
    }
};
