const int MX = 100000;
class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        bitset<MX> dp;
        dp[0] = 1;

        sort(a.begin(), a.end());

        bitset<MX> mask;
        int low = 0;
        for (int &x: a) {
            while(low <= x - 1) {
                mask[low++] = 1;
            }

            dp |= (dp & mask) << x;



        }

        int ans = 0;
        for (int i = 0; i < MX; i++) {
            if (dp[i]) {
                ans = max(ans, i);
            }
        }
        return ans;

    }
};
