typedef long long ll;


class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        int n = a.size();
        sort(all(a));

        bitset<100100> msk;
        int j=0;

        bitset<100100> bs;
        bs[0]=1;

        for(int i=0; i<n; i++) { // 50'000
            for(;j<a[i]; j++) {
                msk.set(j);
            }
            bs = bs | ((bs & msk) << a[i]); // 50'000 / 64 = 1000
        }


        for(int i=100100-1; i>=0; i--) {
            if (bs[i]) return i;
        }
        return 0;

    }
};
