class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        const int N = 1e5;
        bitset<N> bs,mask;
        int n = (int)a.size();
        sort(a.begin(),a.end());
        int j = 0;
        bs[0] = 1;
        for(int i = 0; i < n; i++){
            while(j < a[i]){
                mask[j] = 1;
                j++;
            }
            bs = (bs | ((bs & mask) << a[i]));
        }
        for(int i = N - 1; i >= 0; i--){
            if(bs[i] == 1) return i;
        }
        return 0;

    }
};
