class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        sort(rewardValues.begin(), rewardValues.end());
        int n = rewardValues.size();
        bitset<100005> bs;
        bs[0] = 1;
        for (int i = 0; i < n; i++) {
            bitset<100005> tmp(bs);
            int shift = 100005 - rewardValues[i];
            tmp <<= shift;
            tmp >>= shift;
            tmp <<= rewardValues[i];
            bs |= tmp;
        }
        int ans = 0;
        for (int i = 0; i < 100005; i++) {
            if (!bs[i]) continue;
            ans = max(ans, i);
        }
        return ans;
    }
};
