class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        auto a = rewardValues;
        sort(begin(a), end(a));
        a.resize(unique(begin(a), end(a)) - begin(a));
        int mx = *max_element(begin(a), end(a));
        int n = a.size();
        constexpr int N = 2 * (5e4 + 1) + 1;
        bitset<N> dp;
        dp[0] = true;
        for (int i = 0; i < n; ++i) {
            bitset<N> mask;
            mask.set();
            mask ^= (mask << a[i]);
            dp |= (dp & mask) << a[i];
        }
        for (int r = 2 * mx; r >= 0; --r) {
            if (dp[r]) return r;
        }
        return 0;
    }
};
