class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        int x = 0;
        sort(a.begin(), a.end());
        const int SZ=100100;
        bitset<SZ> b;
        b[0] = 1;
        bitset<SZ> mask;
        int ptr = 0;
        for (int x : a) {
            while (ptr < x) 
                mask.set(ptr++);
            b |= (b & mask) << x;
        }
        int rfl = b.size() - 1;
        while (!b[rfl]) rfl--;
        return rfl;
    }
};
