class Solution:
    def maxTotalReward(self, rewardValues: List[int]) -> int:
        rewardValues.sort()
        bits = 1

        for value in rewardValues:
            bits |= (bits & ((1 << value) - 1)) << value

        ans = 0
        while bits > 1:
            ans += 1
            bits >>= 1

        return ans
