class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        sort(rewardValues.begin(), rewardValues.end());
        int LEN = 100064;
        bitset<100064> bts;
        bts.set(0);
        for (auto it: rewardValues) {
            bts |= (bts << LEN - it) >> LEN - 2 * it;
        }
        for (int i = LEN - 1; i >= 0; --i) {
            if (bts[i]) return i;
        }
        return 0;
    }
};
