class Solution {
public:
    set<int> s;

    vector<int> C;

    bool check(int x) {
        if (C[x])
            return C[x]-1;
        if (x == 0)
        return true;
            auto it = s.lower_bound(x);
            if (it != s.end() && *it == x) {
                C[x] = 2;
                return true;
            }
            while (it != s.begin()) {
                --it;
                if (2 * (*it) <= x) {
                    break;
                }
                if (check(x - *it)) {
                    C[x] = 2;
                    return true;
                }
            }
        C[x] = 1;
        return false;
    }

    int maxTotalReward(vector<int>& a) {
        for (auto x : a) s.insert(x);

        int x = *(--s.end());
        x = x*2-1;
        C.resize(x+1);

        while (!check(x)) {
            --x;
        }
        return x;
    }
};
