class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        const int N = 100001;
        int n = rewardValues.size();
        bitset<N> bs(0);
        bs.set(0);
        bitset<N> aux(0);
        int j = 0;
        vector<int> v = rewardValues;
        sort(v.begin(), v.end());
        for (size_t i = 0; i < n; i++) {
            while (j < v[i]) {
                aux.set(j);
                j++;
            }

            bs |= (bs&aux)<<v[i];
        }

        for (int j = N-1; j >= 0; j--) {
            if (bs.test(j)) {
                return j;
            }
        }

        return 0;
    }
};
