class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        sort(a.begin(), a.end());
        int ans = a.back(); a.pop_back();
        bitset<50100> u, tu;
        u[0] = 1;
        int top = 0;
        for (int i : a) {
            while (top < i) tu[top++] = 1;
            u = u | ((u & tu) << i);
        }
        for (int j = ans - 1; j >= 0; j--) {
            if (u[j]) return j + ans;
        }
        return ans;
    }
};
