const int N = 1e5 + 1;

class Solution {
public:
  int maxTotalReward(vector<int>& a) {
    sort(a.begin(), a.end());
    int n = a.size();
    int m = a[n - 1];
    bitset<N> dp, mask;
    for (int i = 0, j = 0; i < n; i++) {
      dp[a[i]] = 1;
      while (j < a[i]) mask[j] = 1, j++;
      dp |= (dp & mask) << a[i];
    }
    int ans = 0;
    for (int i = N - 1; i >= 0; i--) {
      if (dp[i]) {
        ans = i;
        break;
      }
    }
    return ans;
  }
};
