class Solution {
public:
    int maxTotalReward(vector<int>& a) {
        sort(a.begin(),a.end());
        bitset<100000> dp,msk;
        dp[0]=true;
        msk[0]=true;
        int prv=0;
        for(int x:a){
            while(prv<x){
                msk[prv]=true;
                prv++;
            }
            dp|=(dp&msk)<<x;
        }
        for(int i=dp.size()-1;i>=0;i--)
            if(dp[i])
                return i;
        return -1;
    }
};
