constexpr int MX = 100000;

class Solution {
public:
    int maxTotalReward(vector<int>& A) {
        sort(A.begin(), A.end());
        A.erase(unique(A.begin(), A.end()), A.end());
        bitset<MX + 1> bs, clear;
        bs[0] = 1;
        int cleared_idx = 0;

        for (auto a : A) {
            while (cleared_idx < a)
                clear[cleared_idx++] = 1;
            auto t = bs;
            t &= clear;
            t <<= a;
            bs |= t;
        }

        for (int i = MX; i >= 0; --i) {
            if (bs[i]) return i;
        }

        return 0;
    }
};
