class Solution {
public:
    int maxTotalReward(vector<int>& rewardValues) {
        const long long P=100005;
        bitset<P> bt;
        bt[0]=1;
        int n=rewardValues.size();
        sort(rewardValues.begin(),rewardValues.end());
        for(int i=0;i<n;i++)
        {
            int x=rewardValues[i];
            bt|=(((bt<<(P-x))>>(P-x))<<x);
            // for(int i=0;i<min(4ll,P);i++)cout<<bt[i]<<" ";
            // cout<<endl;
        }
        int ans=0;
        for(int i=0;i<P;i++)if(bt[i])ans=i;
        return ans;
    }
};
